-- Enable legacy mode to create users without C## prefix in CDB
ALTER SESSION SET "_ORACLE_SCRIPT"=TRUE;

-- Create user (Oracle syntax)
CREATE USER otelu IDENTIFIED BY otelp;
GRANT CONNECT, RESOURCE TO otelu;
GRANT CREATE SESSION TO otelu;
GRANT UNLIMITED TABLESPACE TO otelu;

-- Commit user creation to ensure it persists
COMMIT;

-- Create Instana monitoring role
CREATE ROLE INSTANAROLE;

-- Grant privileges to Instana role for monitoring views
GRANT SELECT ON V_$SESSION TO INSTANAROLE;
GRANT SELECT ON V_$BGPROCESS TO INSTANAROLE;
GRANT SELECT ON V_$ACTIVE_SESSION_HISTORY TO INSTANAROLE;
GRANT SELECT ON V_$SYSSTAT TO INSTANAROLE;
GRANT SELECT ON V_$SGASTAT TO INSTANAROLE;
GRANT SELECT ON V_$LIBRARYCACHE TO INSTANAROLE;
GRANT SELECT ON V_$LATCH TO INSTANAROLE;
GRANT SELECT ON V_$ROWCACHE TO INSTANAROLE;
GRANT SELECT ON V_$STATNAME TO INSTANAROLE;
GRANT SELECT ON V_$SESSTAT TO INSTANAROLE;
GRANT SELECT ON V_$PROCESS TO INSTANAROLE;
GRANT SELECT ON V_$SQLAREA TO INSTANAROLE;
GRANT SELECT ON V_$PARAMETER TO INSTANAROLE;
GRANT SELECT ON V_$SQL TO INSTANAROLE;
GRANT SELECT ON V_$VERSION TO INSTANAROLE;
GRANT SELECT ON V_$RESOURCE_LIMIT TO INSTANAROLE;
GRANT SELECT ON V_$SYS_TIME_MODEL TO INSTANAROLE;
GRANT SELECT ON V_$SYSTEM_WAIT_CLASS TO INSTANAROLE;
GRANT SELECT ON V_$INSTANCE TO INSTANAROLE;
GRANT SELECT ON V_$LOCK TO INSTANAROLE;
GRANT SELECT ON DBA_HIST_SQLTEXT TO INSTANAROLE;
GRANT SELECT ON DBA_HIST_SQLSTAT TO INSTANAROLE;
GRANT SELECT ON DBA_HIST_SNAPSHOT TO INSTANAROLE;
GRANT SELECT ON DBA_HIST_ACTIVE_SESS_HISTORY TO INSTANAROLE;
GRANT SELECT ON DBA_USERS TO INSTANAROLE;
GRANT SELECT ON DBA_OBJECTS TO INSTANAROLE;
GRANT SELECT ON DBA_TABLESPACE_USAGE_METRICS TO INSTANAROLE;
