-- Accounting Service: create tables (Oracle doesn't use schemas the same way as PostgreSQL)
-- Tables are created in the user's schema
CREATE TABLE otelu."order" (
    order_id VARCHAR2(4000) PRIMARY KEY
);

CREATE TABLE otelu.shipping (
    shipping_tracking_id VARCHAR2(4000) PRIMARY KEY,
    shipping_cost_currency_code VARCHAR2(4000) NOT NULL,
    shipping_cost_units NUMBER(19) NOT NULL,
    shipping_cost_nanos NUMBER(10) NOT NULL,
    street_address VARCHAR2(4000),
    city VARCHAR2(4000),
    state VARCHAR2(4000),
    country VARCHAR2(4000),
    zip_code VARCHAR2(4000),
    order_id VARCHAR2(4000) NOT NULL,
    CONSTRAINT fk_shipping_order FOREIGN KEY (order_id) REFERENCES otelu."order"(order_id) ON DELETE CASCADE
);

CREATE TABLE otelu.orderitem (
    item_cost_currency_code VARCHAR2(4000) NOT NULL,
    item_cost_units NUMBER(19) NOT NULL,
    item_cost_nanos NUMBER(10) NOT NULL,
    product_id VARCHAR2(4000) NOT NULL,
    quantity NUMBER(10) NOT NULL,
    order_id VARCHAR2(4000) NOT NULL,
    PRIMARY KEY (order_id, product_id),
    CONSTRAINT fk_orderitem_order FOREIGN KEY (order_id) REFERENCES otelu."order"(order_id) ON DELETE CASCADE
);

-- Product Review Service
CREATE TABLE otelu.productreviews (
    id NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    product_id VARCHAR2(16) NOT NULL,
    username VARCHAR2(64) NOT NULL,
    description VARCHAR2(1024),
    score NUMBER(3,1) NOT NULL
);

-- Product Review Service: create index for product_id lookups (ignore if exists)
BEGIN
    EXECUTE IMMEDIATE 'CREATE INDEX product_id_index ON otelu.productreviews (product_id)';
EXCEPTION
    WHEN OTHERS THEN
        IF SQLCODE = -955 THEN
            NULL; -- Index already exists, ignore
        ELSE
            RAISE;
        END IF;
END;
/

